function createHASH(_cmd, _json, _time) {
    console.log("createHASH _cmd:" + _cmd + " _json:" + _json + " _time:" + _time);
    var toHASH = localStorage.version + ":::"
        + _time + ":::"
        + localStorage.imei + ":::"
        + _cmd + ":::"
        + decodeURIComponent(_json) + ":::"
        + localStorage.mobile_key;

    return sha256(md5(toHASH));
}

