function parsePrice(price)
{
    console.log("parsePrice price: " + price.toString());
    price = price.toString();
    //    1    
    if (price.indexOf(".") > 0) {
        var tmp = price.substring(price.indexOf(".") + 1, price.length);
        if (tmp.length == 1) {
            price += "0";
        }
    }

    return price;
}

function normalizeSum(amount) {
    console.log("normalizeSum amount: " + amount.toString());
    amount = amount.toString().replace(',','.');

    //    1    
    if (amount.indexOf(".") > 0) {
        var tmp = amount.substring(amount.indexOf(".") + 1, amount.length);
        if (tmp.length == 1) {
            amount += "0";
        }
    }

    if (amount.indexOf(".") == -1)
        amount += ".00";



    return amount;
}
