function setStatusBarColor(color)
{
    console.log("setStatusBarColor color:" + color);
    if (isMobile && cordova.platformId == 'android') {

        var hex = "#000";

        if (color == "rgba(0, 0, 0, 0)")
        {
            hex = "#000";
        }
        else if (color.indexOf("rgb") == 0)
            hex = rgb2hex(color);
        else if (color.indexOf("#") != -1)
            hex = color;

        StatusBar.backgroundColorByHexString(hex);
    }
}

function __garbageCollector(page) {
    console.log("__garbageCollector page:"+ page);
    switch (page) {
        case mobipayPage.auth:
            console.log("__garbageCollector");
            if (WEBPassport.Views.pin) {
                console.log("__garbageCollector: auth WEBPassport.Views.pin.destroy");

                WEBPassport.Views.pin.stopListening();
                WEBPassport.Views.pin.undelegateEvents();
                WEBPassport.Views.pin.destroy();
            }

            return true;
        case mobipayPage.dashboard:
            console.log("__garbageCollector");
            if (WEBPassport.Views.dashboard) {
                WEBPassport.Views.dashboard.destroy();
                WEBPassport.Views.dashboard.stopListening();
                WEBPassport.Views.dashboard.undelegateEvents();
            }

            if (WEBPassport.Views.auth)
            {
                console.log("__garbageCollector: auth WEBPassport.Views.auth.destroy");
                
                WEBPassport.Views.auth.stopListening();
                WEBPassport.Views.auth.undelegateEvents();
                WEBPassport.Views.auth.destroy();
            }
            if (WEBPassport.Views.otp) {
                console.log("__garbageCollector: otp WEBPassport.Views.otp.destroy");
                
                WEBPassport.Views.otp.stopListening();
                WEBPassport.Views.otp.undelegateEvents();
                WEBPassport.Views.otp.destroy();
            }

            return true;
    }
}

