ons.ready(function () {
    //   AuthSms
    WEBPassport.Views.AuthSms = WEBPassport.IViews.extend({
        el: "#page_auth_sms",
        template: '#template_auth_sms',
        ui: {
            back_btn: "#back_btn", //  

            sms_code: "#sms-code", //   
            new_mobile_input: "#new_mobile_input",// 
            auth_sms_btn: "#auth-sms-btn", //  
            timer: "#timer", // 
            resend_sms_btn: "#resend-sms-btn"
        },
        events: {
            'click @ui.back_btn': 'returnBeforeToMainForm', //   
            'input @ui.sms_code': 'changeCode', //      
            'click @ui.auth_sms_btn': 'sendAuthSms', //   
            'click @ui.resend_sms_btn': 'resendSms', //   reftresh
        },
        initializeAfter: function () {
            console.log("WEBPassport.Views.AuthSms:initializeAfter");
            _.bindAll(this, 'onSuccessAfter', 'smsSuccess')
            this.listenTo(Backbone, "WEBPassport.Views.AuthSms:returnToMainForm", this.returnBeforeToMainForm);
            this.listenTo(Backbone, "WEBPassport.Views.AuthSms:clearScreen", this.clearScreen);
            this.listenTo(Backbone, "WEBPassport.Views.AuthSms:createTimer", this.createTimer);
            var $this = this;

            setTimeout(function () {
                $this.ui.sms_code.focus();
            }, window.settings.isEffects ? timeoutRender : 0);

            if (isMobile)
                listenSMS(this.smsSuccess);

            var result = listMaskPhone.getMaskForPhoneNumber(this.model.attributes.new_mobile);
            this.ui.new_mobile_input.val(this.model.attributes.new_mobile);
            this.ui.new_mobile_input.mask(result.mask, { 'translation': { x: { pattern: /[0-9]/ } } });

            this.$el.i18n();

            this.createTimer();
        },

        returnBeforeToMainForm: function () {
            console.log("WEBPassport.Views.AuthSms:returnBeforeToMainForm");
            var $this = this;

            var callback = function () {
                clearInterval($this.timerId);
            };

            this.returnToMainForm(callback);
        },

        createTimer: function (responseObject) {
            console.log("WEBPassport.Views.AuthSms:resendSms");

            var $this = this;

            if (responseObject)
            {
                $this.model.attributes.responseObject = responseObject;
            }

            $this.ui.sms_code.focus();
            $this.ui.sms_code.val("");
            $this.ui.auth_sms_btn.hide();
            $this.ui.resend_sms_btn.hide();
            $this.ui.timer.show();
            

            var start = moment();
            var millis, seconds, minutes;
            if (jQuery.type(this.ui.timer) != "string") {
                $this.timerId = setInterval(function () {
                    var millis = moment().diff(start);
                    millis = 60000 - millis;
                    seconds = add_zero(parseInt((millis / 1000) % 60));
                    minutes = add_zero(parseInt((millis / 1000) / 60));

                    $this.ui.timer.text(minutes + ':' + seconds);

                    if (seconds <= 1) {
                        clearInterval($this.timerId);
                        $this.ui.resend_sms_btn.show();
                        $this.ui.timer.hide();
                    }

                }, 1000);
            }
        },

        resendSms: function(){
            console.log("WEBPassport.Views.resendSms:initializeAfter");

            Backbone.trigger("WEBPassport.Views.WalletCreate:resendData", "WEBPassport.Views.AuthSms:createTimer");
        },
         
        smsSuccess: function (e) {
            console.log('sms success callback');
            if (e) {
                if (e == 'showAlert') {
                    //navigator.notification.alert(i18n.t('wbs_smscode_copied_label'), null);
                    //stopListenSMS();
                }
                else {
                    this.ui.sms_code.val(e);
                    this.sendAuthSms();
                    stopListenSMS();
                }
            }
        },

        //       
        changeCode: function (e) {
            console.log("WEBPassport.Views.AuthSms:changeCode");
            var $this = this;

            if ($this.ui.sms_code.val().length >= 5) {
                $this.ui.auth_sms_btn.show();
                $this.ui.auth_sms_btn.removeClass("fadeOutUp").addClass("fadeInDown");
            }
            else {
                $this.ui.auth_sms_btn.removeClass("fadeInDown").addClass("fadeOutUp");

                setTimeout(function () {
                    $this.ui.auth_sms_btn.hide();
                }, 100);
            }
        },

        //   
        sendAuthSms: function () {
            console.log("WEBPassport.Views.AuthSms:sendAuthSms");
            //TODO  
            var sms = this.ui.sms_code.val();
            //var payment = 2;
            //var currency = 'UAH';
            var payment = 1;
            var currency = getCurrencyLoyalty(this.model.attributes.new_mobile);

            //switch (this.model.attributes.newWalletOption) {
            //    case "newLoyalty":
            //        payment = 1;
            //        currency = '';
            //}

            WEBPassport.requestModel.authSms(localStorage.session, payment, sms, currency, this.model.attributes.responseObject);
            WEBPassport.requestModel.sandData(this.onSuccessAfter);
        },

        onSuccessAfter: function (model, response, options) {
            console.log("WEBPassport.Views.AuthSms:onSuccessAfter");


            WEBPassport.mainViewModel.attributes.mobile = this.model.get('new_mobile');
            if (!WEBPassport.mainViewModel.attributes.fname && this.options.fname) {
                WEBPassport.mainViewModel.attributes.fname = this.options.fname;
            }
            if (!WEBPassport.mainViewModel.attributes.lname && this.options.lname) {
                WEBPassport.mainViewModel.attributes.lname = this.options.lname;
            }
            var $this = this;

            if ($this.timerId)
                clearInterval($this.timerId);

            switch (this.model.attributes.newWalletOption) {
                case 'P2P':
                case 'cardPayment':
                case "card":
                    Backbone.trigger("WEBPassport.Views.WalletCreate:clearScreen");
                    this.returnToMainForm();
                    Backbone.trigger("WEBPassport.Views.MyCards:checkMandatoryDataForCard");
                    break;
                case "newWallet":
                    Backbone.trigger("WEBPassport.Views.WalletCreate:clearScreen");
                    this.returnToMainForm();
                    Backbone.trigger("WEBPassport.Views.Wallet.Wallet_Item:updateWallet");
                    Backbone.trigger("WEBPassport.Views.Wallet.Wallet_Item:updateHistory");
                    break;
                case "newLoyalty":
                    Backbone.trigger("WEBPassport.Views.WalletCreate:clearScreen");
                    this.returnToMainForm();
                    Backbone.trigger("WEBPassport.Views.Loyalty.Wallet_Item:updateWallet");
                    Backbone.trigger("WEBPassport.Views.Loyalty.Wallet_Item:updateHistory");
                    break;
                case "telegram":
                    Backbone.trigger("WEBPassport.Views.WalletCreate:clearScreen");
                    this.returnToMainForm();
                    Backbone.trigger('WEBPassport.Views.ModuleTelegramController:openFrame');
                    break;
                case "auth":
                    Backbone.trigger("WEBPassport.Views.WalletCreate:clearScreen");
                    this.returnToMainForm();

                    if (response.data)
                    {
                        localStorage.phone = response.data.phone;
                        localStorage.token = response.data.token;
                        localStorage.user_id = response.data.user_id;
                    }

                    mobipayNavigatorOpen(mobipayPage.auth, "none");
                    break;
                case "newDreamClub":

                    function callback() {
                        Backbone.trigger("WEBPassport.Views.WalletCreate:clearScreen");
                        $this.returnToMainForm();
                    }
                    Backbone.trigger('WEBPassport.Views.DreamClubWidget:authDreamClub', callback);
                    break;
                case "presents":
                    Backbone.trigger("WEBPassport.Views.WalletCreate:clearScreen");
                    this.returnToMainForm();
                    break;
            }
        }
    });
});
